from reportlab.lib.pagesizes import letter
from reportlab.lib.styles import getSampleStyleSheet
from reportlab.platypus import SimpleDocTemplate, Paragraph, Spacer, Table, Image
from reportlab.lib.units import inch

# Configurações do documento
doc = SimpleDocTemplate("meu_relatorio.pdf", pagesize=letter)

# Estilos
styles = getSampleStyleSheet()
title_style = styles["Title"]
paragraph_style = styles["Normal"]

# Conteúdo do documento
content = []
logo = "data:image/jpeg;base64,"
imagem_do_logo = Image(logo, 1*inch, 1*inch)
content.append(imagem_do_logo)

# Título
title_text = "<u>Título do Relatório</u>"
title_paragraph = Paragraph(title_text, style=title_style)
content.append(title_paragraph)
content.append(Spacer(1, 20))  # Espaço de 20pt

# Parágrafo
paragraph_text = "Este é um parágrafo de exemplo."
paragraph = Paragraph(paragraph_text, style=paragraph_style)
content.append(paragraph)
content.append(Spacer(1, 15))  # Espaço de 15pt

# Tabela
data = [
    ["Coluna 1", "Coluna 2", "Coluna 3", "Coluna 4"],
    ["Dado 1", "Dado 2", "Dado 3", "Dado 4"]
]
table = Table(data)
content.append(table)

# Construir o documento
doc.build(content)
